package org.armzbot.entity;import jakarta.persistence.Column;import jakarta.persistence.Entity;import jakarta.persistence.JoinColumn;import jakarta.persistence.ManyToOne;import lombok.Data;import lombok.EqualsAndHashCode;import java.io.Serializable;@EqualsAndHashCode(callSuper = true)@Data@Entity(name = "accommodation")public class Accommodation extends BaseEntity implements Serializable {    @Column()    private String acc_name;    @Column()    private String images;    @Column()    private String room_type;    @Column()    private int minimum_nights;    @Column()    private int maximum_nights;    @Column()    private float price;    @Column()    private String description;    @Column()    private String room_address;    @Column()    private String room_street;    @Column()    private String room_state;    @Column()    private String room_country;    @Column()    private String room_country_code;    @Column()    private String cancellation_policy;    @Column()    private double location_lat;    @Column()    private double location_long;    @Column()    private int bathrooms;    @Column()    private int bedroom;    @ManyToOne    @JoinColumn(name = "id", nullable = false)    private User user;}